var miliseconds = new Date().getTime();
//console.log('miliseconds: ' + miliseconds);
var userId = UserContext.userId;
//console.log('userId: ' + userId);
var businessIdentifier = 'IPC' + miliseconds.toString() + userId;
//console.log('BI: ' + businessIdentifier);
CS.setAttribute("Business_Identifier_0", businessIdentifier);

//iframe not flickering
//only refresh when loaded
CS.CLIENTCUSTOM = CS.CLIENTCUSTOM || {};
CS.CLIENTCUSTOM.IframeManager = CS.CLIENTCUSTOM.IframeManager || (function(){
var module = {};
var oldValue = CS.getAttributeValue('ConfigId_0');
var setIframe = function(){
var iframeHtml = '<iframe frameborder="0" width="100%" height="250" scrolling="auto" src="https://c.cs7.visual.force.com/apex/deleteUser?id\='+CS.getAttributeValue('ConfigId_0')+'"></iframe>';
CS.setTextDisplay('iFrameAllDeleteUsers_0', iframeHtml);
//console.log('iframe: ' + iframeHtml);
};
if(oldValue){
setIframe();
}
var setOldValue = function(value){
oldValue = value;
}
var applyIframe = function(){
var newValue = CS.getAttributeValue('ConfigId_0');
if (oldValue != newValue) {
if (newValue) {
setIframe();
}
oldValue = newValue;
}
}
//expose to public
module.applyIframe = applyIframe;
module.setOldValue = setOldValue;
return module;
})();
CS.CLIENTCUSTOM.IframeManager.applyIframe();

var rDate=CS.getAttributeValue('Requested_Activation_Date_0'); 

if(rDate != ''){ 
console.log('rDate======'+rDate); 
var dateArray = rDate.split('/'); 
console.log('dateArray======'+dateArray+'======'+dateArray[2]+'======'+dateArray[1]+'======'+dateArray[0]); 
var today = new Date(); 

var userSelectedDate = new Date(dateArray[2], dateArray[1]-1, dateArray[0], '23', '59', '59'); 
console.log(today+'======'+userSelectedDate); 
if(+userSelectedDate < +today){ 


console.log('======'+'validation'); 
CS.setAttribute('Requested_Activation_Date_Validation_0', 'Yes'); 
}else{ 
console.log('======'+'novalidation'); 
CS.setAttribute('Requested_Activation_Date_Validation_0', 'No'); 
CS.setAttribute('Requested_Activation_Date_US_0', dateArray[1]+'/'+dateArray[0]+'/'+dateArray[2]); 
} 
}

//Hide Finish button if no IPC Number
//-----------------------
var ipcnumber = CS.getAttributeValue('IPC_Number_0');
if (ipcnumber && ipcnumber.trim()) {
jQuery('.CSFinishButton').show();
} else {
jQuery('.CSFinishButton').hide()
}
